    /**
     * @param $language
     * @return string
     */
    private function getRandomTags($language)
    {
        $numberOfTags = rand(2, 6);
        $i = 0;
        $string = '';
        while ($i < $numberOfTags) {
            $id = rand(1, 15);
            if ($tag = Tag::findOne($id)) {
                $string .= $tag['name_' . $language] . ',';
                $i++;
            }
        }
        return $string;
    }

