    /**
     * @param int $number
     */
    public function actionGenerateArticles($number = 0)
    {
        $i = 0;
        $faker = Factory::create();
        while ($i < $number) {
            $art = new Article();
            $art->setAttributes([
                'language_id' => 'ca',
                'user_id' => 1,
                'category_id' => $faker->numberBetween(1, 10),
                'date' => $faker->date('Y-m-d H:i:s'),
                'title' => $faker->text(120),
                'resume' => $faker->text(180),
                'content' => $this->getContent(),
                'state' => 1,
                'tags_form' => $this->getRandomTags('ca'),
            ]);
            if ($art->save() && $art->findOne($art->id)->save()) {
                $artEN = new Article();
                $artEN->setAttributes([
                    'translation_of' => $art->id,
                    'language_id' => 'en',
                    'user_id' => 1,
                    'category_id' => $faker->numberBetween(1, 10),
                    'date' => $faker->date('Y-m-d H:i:s'),
                    'title' => $faker->text(120),
                    'resume' => $faker->text(180),
                    'content' => $this->getContent(),
                    'state' => 1,
                    'tags_form' => $this->getRandomTags('en')
                ]);
                $artEN->save();
                $artEN->findOne($artEN->id)->save();

                $artES = new Article();
                $artES->setAttributes([
                    'translation_of' => $art->id,
                    'language_id' => 'es',
                    'user_id' => 1,
                    'category_id' => $faker->numberBetween(1, 10),
                    'date' => $faker->date('Y-m-d H:i:s'),
                    'title' => $faker->text(120),
                    'resume' => $faker->text(180),
                    'content' => $this->getContent(),
                    'state' => 1,
                    'tags_form' => $this->getRandomTags('es')
                ]);
                $artES->save();
                $artES->findOne($artES->id)->save();
            }
            $i++;
        }
    }


}
